INSERT INTO medicamentos (nome, para_que_serve, contraindicacoes, armazenamento, preco, id_pais) VALUES
("Neosoro","Indicado para a descongestão nasal, em adultos e crianças.","Contraindicado para pacientes portadores de alguma doença ou em tratamento com medicamentos que promovam a retenção de sódio e para pacientes com alergia a algum dos componentes da fórmula.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc).", 8.40, (SELECT id FROM paises WHERE nome = "Brasil")),
("Puran T4","Indicado para reposição ou suplementação hormonal em casos de hipotireoidismo ou de supressão do hormônio TSH da hipófise, em adultos e crianças.","Contraindicado para pacientes com insuficiência da glândula supra-renal e para pacientes com alergia a algum dos componentes da fórmula.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc).", 13.84, (SELECT id FROM paises WHERE nome = "Brasil")),
("Microvlar","Pílula anticoncepcional indicada para prevenir a gravidez, em mulheres.","Está contraindicada para mulheres grávidas, que possam estar grávidas ou amamentando e para pacientes com historial de coágulo em qualquer veia.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc).", 7.64, (SELECT id FROM paises WHERE nome = "Brasil")),
("Dorflex","Indicado para o alivio de dores cabeça tensionais e de dores associadas a contraturas musculares.","Contraindicado para mulheres grávidas ou amamentando, crianças com menos de 12 anos de idade, pacientes com glaucoma, obstrução pilórica ou duodenal.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc).", 5.02, (SELECT id FROM paises WHERE nome = "Brasil")),
("Glifage","Indicado para o tratamento da diabetes do tipo 2 ou no tratamento da diabetes tipo 1 quando associado ao tratamento com insulina, em adultos.","Contraindicado para pacientes com problema de funcionamento do fígado ou dos rins, diabetes não controlada, com hiperglicemia ou cetoacidose graves.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc)", 6.29, (SELECT id FROM paises WHERE nome = "Brasil")),
("Hipoglós","Indicado para proteger a pele do bebê contra assaduras da fralda, em bebês e crianças.","Caso os sintomas não desapareçam ou piorem, deve interromper o uso da pomada e consultar o médico.","Conservar em temperatura ambiente (ambiente com temperatura entre 15 e 30ºc)", 30.59, (SELECT id FROM paises WHERE nome = "Brasil"));
